CREATE TABLE author (
    id integer NOT NULL,
    author_fio character varying(128)
);
CREATE TABLE book (
    id_book bigint NOT NULL,
    barcode_book bigint NOT NULL,
    title character varying(256),
    author bigint,
    dopauthor bigint,
    dopauthor2 bigint,
    publish character varying(128),
    text text,
    totalpages character varying(8),
    location character varying(32),
    bbk character varying(32),
    isbn character varying(32),
    book_leng integer,
    status integer,
    year character varying(16),
    inventar character varying(128),
    category bigint,
    dateinventar date,
    datereg date,
    price real,
    doc_id bigint,
    place_publish character varying(128));


CREATE TABLE category (
    id bigint NOT NULL,
    parent_id bigint,
    type smallint,
    category_name character varying(127)
);

CREATE TABLE department (
    id integer NOT NULL,
    departmentName character varying(512) NOT NULL
);

CREATE TABLE event (
    id integer NOT NULL,
    barcode_book bigint,
    barcode_reader bigint,
    date_start date,
    date_finish date,
    status integer
);
CREATE TABLE language (
    id integer NOT NULL,
    language_name character varying(63)
);

CREATE TABLE organization (
    id integer NOT NULL,
    organizationName character varying(256)
);

CREATE TABLE reader (
    id integer NOT NULL,
    barcode bigint NOT NULL,
    surname character varying(20),
    name character varying(20),
    patronymic character varying(20),
    datebirth date,
    organization integer,
    department integer,
    position character varying(40),
    address character varying(128),
    telephone character varying(32),
    login character varying(12),
    password character varying(64),
    url_pic character varying(8)
);










